import React, { useState } from 'react';
import './App.css';
import Card from './Components/Card'

function App() {
  const [ flippedCards, setFlippedCard ] = useState([])
  const handleClickCard = (id) => setFlippedCard([...flipped, [id]])

  return (
    <div className="App">
      <header className="App-header">
        <h1>MeMOOry</h1>
      </header>
        <Card
          id={1}
          name="Bill"
          isFlipped={flippedCards.includes(1)}
          handleClickCard={() =>handleClickCard(1)}
          /> 
    </div>
  );
}

export default App;
